CREATE database IF NOT EXISTS WebPsicoDB;
USE WebPsicoDB;
CREATE TABLE if not exists CONTATO (ID INTEGER AUTO_INCREMENT NOT NULL, CPF VARCHAR(255), DATANASCIMENTO DATE, EMAIL VARCHAR(255), ESCOLARIDADE VARCHAR(255), ESTADOCIVIL VARCHAR(255), NACIONALIDADE VARCHAR(255), NATURALIDADE VARCHAR(255), NOME VARCHAR(255), OBS VARCHAR(255), PARENTESCO VARCHAR(255), PROFISSAO VARCHAR(255), RELIGIAO VARCHAR(255), SEXO VARCHAR(255), TELEFONECELULAR VARCHAR(11), TELEFONEFIXO VARCHAR(10), ENDERECO_IDENDERECO INTEGER, PRIMARY KEY (ID));
CREATE TABLE if not EXISTS PACIENTE (ID INTEGER AUTO_INCREMENT NOT NULL, CPF VARCHAR(255), DATAINICIO DATE, DATANASCIMENTO DATE, DATAULTIMASESSAO DATETIME, DESATIVADO TINYINT(1) default 0, EMAIL VARCHAR(255), ESCOLARIDADE VARCHAR(255), ESTADOCIVIL VARCHAR(255), NACIONALIDADE VARCHAR(255), NATURALIDADE VARCHAR(255), NOME VARCHAR(255), OBS VARCHAR(255), PRECO DOUBLE, PREFERENCIATURNO VARCHAR(255), PROFISSAO VARCHAR(255), SEXO VARCHAR(255), TELEFONECELULAR VARCHAR(11), TELEFONEFIXO VARCHAR(10), INDICACAO_IDINDICACAO INTEGER, ENDERECO_IDENDERECO INTEGER, PRIMARY KEY (ID));
CREATE TABLE if not EXISTS USUARIO (ID INTEGER AUTO_INCREMENT NOT NULL, DESATIVADO TINYINT(1) default 0, EMAIL VARCHAR(255), LOGIN VARCHAR(255) NOT NULL UNIQUE, NOMECOMPLETO VARCHAR(255), PERFIL VARCHAR(255), SENHA VARCHAR(255), PRIMARY KEY (ID));
CREATE TABLE if not EXISTS INDICACAO (IDINDICACAO INTEGER AUTO_INCREMENT NOT NULL, NOME VARCHAR(255), PROFISSAO VARCHAR(255), PRIMARY KEY (IDINDICACAO));
CREATE TABLE if not EXISTS ENDERECO (IDENDERECO INTEGER AUTO_INCREMENT NOT NULL, BAIRRO VARCHAR(255), CEP VARCHAR(255), CIDADE VARCHAR(255), COMPLEMENTO VARCHAR(255), LOGRADOURO VARCHAR(255), NUMERO VARCHAR(255), UF VARCHAR(255), PRIMARY KEY (IDENDERECO));
CREATE TABLE if not EXISTS ANAMNESE (ID INTEGER AUTO_INCREMENT NOT NULL, ACIDENTE VARCHAR(255), ALERGIA VARCHAR(255), ALIMENTACAO VARCHAR(255), ATITUDE VARCHAR(255), CIRURGIA VARCHAR(255), CONCIENTE VARCHAR(255), ESCOLA VARCHAR(255), EXAMEPSICO VARCHAR(255), EXPANSAO VARCHAR(255), FAMILIA VARCHAR(255), GRAVIDEZ VARCHAR(255), HIPOTESE VARCHAR(255), HUMOR VARCHAR(255), IRESCOLA VARCHAR(255), LINGUAGEM VARCHAR(255), MANIA VARCHAR(255), MEDICAMENTO VARCHAR(255), NEGACAO VARCHAR(255), OBS VARCHAR(255), QUEIXA VARCHAR(255), RESUMO VARCHAR(255), RETRATO VARCHAR(255), SAUDE VARCHAR(255), SINTOMA VARCHAR(255), SONO VARCHAR(255), TRATAMENTO VARCHAR(255), VIDAPESSOAL VARCHAR(255), PACIENTE_ID INTEGER, PSICOLOGA_ID INTEGER, PRIMARY KEY (ID));
CREATE TABLE if not EXISTS EVOLUCAO (ID INTEGER AUTO_INCREMENT NOT NULL, DATA DATETIME, TEXTO VARCHAR(500), PACIENTE_ID INTEGER, PSICOLOGA_ID INTEGER, PRIMARY KEY (ID));
CREATE TABLE if not EXISTS COMPROMISSO (ID INTEGER AUTO_INCREMENT NOT NULL, DATAMARCADA DATETIME, PACIENTE_ID INTEGER, PSICOLOGA_ID INTEGER, PRIMARY KEY (ID));
CREATE TABLE if not EXISTS PACIENTE_CONTATO (contatos_ID INTEGER NOT NULL, pacientes_ID INTEGER NOT NULL, PRIMARY KEY (contatos_ID, pacientes_ID));



















